/* user method - skeleton.sed */
protected final boolean containsTagName(String markerTagName) {
	Iterator blocks = fBlockMarkers.iterator();
	while(blocks.hasNext()) {
		BlockMarker marker = (BlockMarker)blocks.next();
		if(marker.isCaseSensitive()) {
			if(marker.getTagName().equals(markerTagName))
				return true;
		}
		else {
			if(marker.getTagName().equalsIgnoreCase(markerTagName))
				return true;
		}
	}
	return false;
}

/* user method - skeleton.sse */
protected final String findTwigDelimiter(String text, String insideOf, String[] delimiters, String delimType, int afterDelimType){

	
	for (String delim : delimiters) {
		
		final int startChar = text.indexOf(delim);
		
		System.err.println("TOKEN: delim: "+delim+" startchar: "+startChar);
		
		if (startChar != -1) {
			//System.err.println("a");
			//go to the char right before the text, so it doesnt mark the whole thing red...
			if (startChar > 0){
				//System.err.println("b");
				//push to right before the delim
				System.err.println("push back");
				yypushback(yylength() - startChar);
				//return YYINITIAL;
			} else {
				//System.err.println("c");
				yybegin(afterDelimType);
				// pushback to just after the closing bracket
				int delemLen = delim.length();
				yypushback(yylength() - startChar - delemLen);
				
				System.err.println("found something");
				return delimType;
			}
		}
		
	}
	
	System.err.println("nothing found, return default");
	System.err.println();
	
	return insideOf;
}


