  /**
   * Pushes the specified amount of characters back into the input stream.
   *
   * They will be read again by then next call of the scanning method
   *
   * @param number  the number of characters to be read again.
   *                This number must not be greater than yylength()!
   */
  void yypushback(int number) {
    if ( number > yylength() )
      yy_ScanError(YY_PUSHBACK_2BIG);

    yy_markedPos -= number;
  }

	/**
	 * user method - skeleton.sed
	 */
	protected final boolean containsTagName(char[] markerTagName, int offset, int tagnameLength) {
		for(int j = 0; j < fBlockMarkers.size(); j++) {
			BlockMarker marker = (BlockMarker)fBlockMarkers.get(j);
			if(marker.getTagName().length() == tagnameLength) {
				boolean matchesSoFar = true;
				for(int i = 0; i < tagnameLength && matchesSoFar; i++) {
					if(marker.isCaseSensitive()) {
						if(marker.getTagName().charAt(i) != markerTagName[i + offset])
							matchesSoFar = false;
					}
					else {
						if(Character.toLowerCase(marker.getTagName().charAt(i)) != Character.toLowerCase(markerTagName[i + offset]))
							matchesSoFar = false;
					}
				}
				if(matchesSoFar)
					return true;
			}
		}
		return false;
	}
	
	/* user method - skeleton.sse */
	protected final String findTwigDelimiter(String text, String insideOf, String delim, String delimType, int afterDelimType){
		
		final int startChar = text.indexOf(delim);
		
		if (Debug.debugTokenizer) {
//			System.err.println("Delim: "+delim+" looking in: '"+text+"' startchar: "+startChar);
		}
			
		if (startChar != -1) {
			//System.err.println("a");
			//go to the char right before the text, so it doesnt mark the whole thing red...
			if (startChar > 0){
//				System.err.println("b");
				//push to right before the delim
				yypushback(yylength() - startChar);
				//return YYINITIAL;
			} else {
//				System.err.println("c");
				yybegin(afterDelimType);
				// pushback to just after the closing bracket
				int delemLen = delim.length();
				yypushback(yylength() - startChar - delemLen);
				return delimType;
			}
		}
		
		
		delim = "{%";
		final int startChar3 = text.indexOf(delim);
		
		if (Debug.debugTokenizer) {
//			System.err.println("Delim: "+delim+" looking in: '"+text+"' startchar: "+startChar);
		}
			
		if (startChar3 != -1) {
			//System.err.println("a");
			//go to the char right before the text, so it doesnt mark the whole thing red...
			if (startChar3 > 0){
//				System.err.println("b");
				//push to right before the delim
				yypushback(yylength() - startChar3);
				//return YYINITIAL;
			} else {
//				System.err.println("c");
				yybegin(afterDelimType);
				// pushback to just after the closing bracket
				int delemLen = delim.length();
				yypushback(yylength() - startChar3 - delemLen);
				return TWIG_STMT_OPEN;
			}
		}
		
		
		
		delim = "{#";
		final int startChar2 = text.indexOf(delim);
		
		if (Debug.debugTokenizer) {
//			System.err.println("Delim: "+delim+" looking in: '"+text+"' startChar2: "+startChar2);
		}
			
		if (startChar2 != -1) {
		
			if (Debug.debugTokenizer)		
				System.err.println("aa");
				
			//go to the char right before the text, so it doesnt mark the whole thing red...
			if (startChar2 > 0){
			
				if (Debug.debugTokenizer)
					System.err.println("bb");
					
				//push to right before the delim
				yypushback(yylength() - startChar2);
				//return YYINITIAL;
			} else {
			
				if (Debug.debugTokenizer)
					System.err.println("cc");

				fEmbeddedHint = TWIG_COMMENT_TEXT;
				fEmbeddedPostState = ST_TWIG_COMMENT;


				yybegin(ST_TWIG_COMMENT);
				// pushback to just after the closing bracket
				int delemLen = delim.length();
				yypushback(yylength() - startChar2 - delemLen);
				return TWIG_COMMENT_OPEN;
			}
		}
		
		//System.err.println("d"+insideOf);
		return insideOf;
	}

		

	/**
	 * user method - skeleton.sed
	 *
	 * Return ALL of the regions scannable within the remaining text
	 * Note: for verification use
	 */
	public final List getRegions() {
		List tokens = new ArrayList();
		ITextRegion region = null;
		try {
			region = getNextToken();
			while(region != null) {
				if (region != null) {
					tokens.add(region);
				}
				region = getNextToken();
			}
		}
		catch (StackOverflowError e) {
			Logger.logException(getClass().getName()+": input could not be tokenized correctly at position " + getOffset(), e);//$NON-NLS-1$
			throw e;
		}
		catch (Exception e) {
			// Since this is convenience method and NOT the recommended 
			// way of getting tokens, many errors are simply hidden
			Logger.logException("Exception not handled retrieving regions: " + e.getLocalizedMessage(), e);//$NON-NLS-1$
		}
		return tokens;
	}
